import * as handTrack from 'handtrackjs';
import { React, useEffect, useState } from 'react'

export default function Handtrack() {

    var video = document.getElementById("video");
    const [loading, setLoading] = useState(0);
    let myModel = null;

    handTrack.startVideo(video);

    const loadTracker = () => {
        handTrack.load()
            .then(model => {
                myModel = model;
                console.log("model loaded");
                setLoading(1);
            });
    }

    const runTracker = () => {

        var video = document.getElementById("video");
        var canvas = document.getElementById("canvas");
        var context = canvas.getContext('2d')
        var mediasource = video;

        if (myModel != null) {
            myModel.detect(video).then(predictions => {
                myModel.renderPredictions(predictions, canvas, context, mediasource);
            });
        }
    }

    const [time, setTime] = useState(0);
    useEffect(() => {
        const timer = setTimeout(() => {
            setTime(time+1);
        }, 500);
        return () => clearTimeout(timer);
    })

    const render = ()=>{

        if (loading === 0) loadTracker();
        var video = document.getElementById("video");
        video.addEventListener('loadeddata', () => {runTracker();})
    }

    useEffect(() => {
        render();
        var video = document.getElementById("video");
        return () => video.removeEventListener('loadeddata', render);
    })

    return (
        <canvas id="canvas"></canvas>
    )
}
